export default function Profile({ permissions }) {
  const [selectedMenuKey, setSelectedMenuKey] = useState('1')
  const userFromLocalStorage = JSON.parse(localStorage.getItem('userInfo'))
  const userNameLogin = userFromLocalStorage?.login || 'none'
  const avatar = userFromLocalStorage?.avatar || DefaultAvatar
  const [form] = Form.useForm()
  const canView = checkActionPermission(permissions, 'setting', 'view')

  const userId = userFromLocalStorage.id
  const page = 1
  const pageSize = 100
  const { t } = useTranslation()

  const handleMenuClick = (e) => {
    setSelectedMenuKey(e.key)
  }
  const handleSettingsClick = () => {
    setSelectedMenuKey('4')
  }
  const handleFinish = (values) => {
    console.log('User Info:', values)
  }
  const handleLogout = () => {
    localStorage.removeItem('userInfo')
    Cookies.remove('accessToken')
    window.location.href = '/u/login'
  }
  // Menu items
  const menu = (
    <Menu onClick={handleMenuClick} style={{ width: 200 }}>
      <Menu.Item key="1">{t('phone_profile.menu_item_1')}</Menu.Item>
      <Menu.Item key="2">{t('phone_profile.menu_item_2')}</Menu.Item>
      <Menu.Item key="3">{t('phone_profile.menu_item_3')}</Menu.Item>
      <Menu.Item key="logout_5" style={{ color: 'red' }} onClick={handleLogout}>
        {t('phone_profile.menu_item_4')}
      </Menu.Item>
    </Menu>
  )
  return (
    <div className="w-full h-screen bg-white">
      <Helmet>
        <title>{userNameLogin}</title>
      </Helmet>
      <div className="h-full pb-20 lg:pb-4">
        <div className="h-full p-3 overflow-auto scrollable-content">
          <header className="flex items-center justify-end  mb-5">
            <div className="flex items-center gap-4">
              {canView === true && (
                <button type="button" onClick={handleSettingsClick}>
                  <SettingIcon />
                </button>
              )}

              <Dropdown overlay={menu} trigger={['click']}>
                <button type="button">
                  <MenuIcon />
                </button>
              </Dropdown>
            </div>
          </header>
          {selectedMenuKey === '1' && (
            <>
              <div className="flex items-center  mb-4">
                <Avatar
                  size={64}
                  src={avatar}
                  icon={<img src={DefaultAvatar} alt="Default Avatar" />}
                  className="mr-4"
                />
                <div>
                  <Title level={5}>
                    {userFromLocalStorage?.login || 'none'}
                  </Title>
                  <span className="">
                    {' '}
                    {t('personal_settings_key_menu_01.your_personal_account')}
                  </span>
                </div>
              </div>
              <Card style={{ marginBottom: '20px' }}>
                <Title level={5}>{t('Thông tin cá nhân')}</Title>
                <ul className="space-y-1">
                  <li>
                    <a className="flex items-center gap-2 rounded-lg  py-2 text-gray-500  hover:text-gray-500 ">
                      <UserIcon />

                      <span className="text-sm font-medium">
                        {' '}
                        Hoàng Văn Tuấn{' '}
                      </span>
                    </a>
                  </li>
                  <li>
                    <a className="flex items-center gap-2 rounded-lg  py-2 text-gray-500  hover:text-gray-500 ">
                      <IdIcon />

                      <span className="text-sm font-medium"> NV09876532 </span>
                    </a>
                  </li>
                  <li>
                    <a className="flex items-center gap-2 rounded-lg  py-2 text-gray-500  hover:text-gray-500 ">
                      <MailIcon />

                      <span className="text-sm font-medium">
                        {' '}
                        tuanvhoang31@gmail.com{' '}
                      </span>
                    </a>
                  </li>
                  <li>
                    <a className="flex items-center gap-2 rounded-lg  py-2 text-gray-500  hover:text-gray-500 ">
                      <CallIcon />

                      <span className="text-sm font-medium"> 0987654321 </span>
                    </a>
                  </li>
                </ul>
              </Card>
              <Card style={{ marginBottom: '20px' }}></Card>
            </>
          )}
          {selectedMenuKey === '2' && <KeyMenu02 />}
          {selectedMenuKey === '3' && <KeyMenu03 />}
          {selectedMenuKey === '4' && canView && <PhoneGeneralSettings />}
        </div>
      </div>
    </div>
  )
}
